<script src="http://cdn.jsdelivr.net/jquery.validation/1.14.0/jquery.validate.min.js"></script>
<script type="text/javascript"
        src="http://cdnjs.cloudflare.com/ajax/libs/jquery-validate/1.12.0/additional-methods.js"></script>
<script type="text/javascript" src="//cdnjs.cloudflare.com/ajax/libs/gsap/1.18.0/TweenMax.min.js"></script>
<script src="<?php echo base_url('public/js/jquery.fancybox/jquery.fancybox.js'); ?>"></script>

<script>
    loadCSS('jquery_fancybox', "<?php echo base_url('public/js/jquery.fancybox/jquery.fancybox.css'); ?>");
    loadCSS('jquery_fancybox_custom_style', "<?php echo base_url('public/js/jquery.fancybox/custom-style.css'); ?>");

    $(document).ready(function () {

        $('#icon-THISMONTH').parent().click(function (e) {
            e.preventDefault();
        });

        $('#icon-THISMONTH').click(function (e) {
            e.preventDefault();

            $('#showPopupImageSpecialMonth').fancybox({
                openEffect: 'elastic',
                closeEffect: 'elastic',
                helpers: {
                    title: {
                        type: 'inside'
                    }
                }
            }).trigger('click');
        });

        $('#icon-apsara').parent().click(function (e) {
            e.preventDefault();
        });

        $('#icon-apsara').click(function (e) {
            e.preventDefault();

            $('#showPopupImageAbsara').fancybox({
                openEffect: 'elastic',
                closeEffect: 'elastic',
                helpers: {
                    title: {
                        type: 'inside'
                    }
                }
            }).trigger('click');
        });

    });

    $('#book_a_table').click(function (e) {
        e.preventDefault();

        $('#menus_container').hide();
        $('#book_a_table_form').show();
        $('#book_a_table_form').find('input[type=text], textarea').val('');
        $('#name_of_reservation input').focus();
    });

    $('a').click(function () {
        $('#menus_container').show();
        $('#book_a_table_form').hide();
    });

    $('#btn_cancel_book_a_table').click(function (e) {
        e.preventDefault();

        $('#menus_container').show();
        $('#book_a_table_form').hide();
    });

    $(document).ready(function () {
        var minDate = new Date();

        $('#reservation_date input').Zebra_DatePicker({
            direction: 0
        });

        $('#reservation_time_input').timepicki({increase_direction: 'up'});
    });

    /**
     * Validation book a table form
     */
    $(function () {
        var form = $('#book_a_table_form form');

        form.validate({
            rules: {
                name_of_reservation: {
                    required: true
                },
                email_address: {
                    required: true,
                    email: true
                },
                date: {
                    required: true
                },
                time: {
                    required: true
                },
                number_of_guests: {
                    required: true,
                    number: true,
                    min: 1
                }
            },
            messages: {
                "name_of_reservation": {
                    required: 'Required'
                },
                "email_address": {
                    required: 'Required',
                    email: 'Invalid email'
                },
                "date": {
                    required: 'Required'
                },
                "time": {
                    required: "Required"
                },
                "number_of_guests": {
                    required: 'Required',
                    number: "Not a number",
                    min: 'Invalid value'
                }
            },
            errorPlacement: function (error, element) {
                var placement = element.closest('.form-control').find('.message_error');
                if (placement) {
                    $(placement).append(error)
                } else {
                    error.insertAfter(element);
                }
            }
        });

        $('#btn_submit_book_a_table').unbind("click").click(function (e) {
            e.preventDefault();

            if (form.valid()) {
                var btn = $(this);

                btn.prop('disabled', true);
                $('#loading_container').show();

                form.hide();

                var action_url = form.attr('action');
                var data = form.serialize();

                $.ajax({
                    url: action_url,
                    type: 'POST',
                    data: data,
                    success: function (response) {
                        if (response.error == false) {
                            window.location = "<?php echo base_url(); ?>booking/thank-you.html";
                        } else {
                            $('#book_a_table_form_error_message_container').show();

                            form.show();
                        }

                        $('#loading_container').hide();
                        btn.prop('disabled', false);
                    }
                });
            }
        });

    });


    $(document).ready(function () {
        var $red = document.querySelector('.loading_dot_red');
        var $green = document.querySelector('.loading_dot_green');
        var $blue = document.querySelector('.loading_dot_blue');
        var $purple = document.querySelector('.loading_dot_purple');
        var start = 0;

        var tl = new TimelineMax({
            repeat: -1,
            repeatDelay: 0.05,
        });
        // center
        tl.fromTo($green, 0.4, {x: 0}, {x: 30, ease: Linear.easeNone}, start);
        tl.fromTo($blue, 0.4, {x: 0}, {x: -30, ease: Linear.easeNone}, start);
        // ext 1st
        tl.to($green, 0.4, {x: 60, ease: Linear.easeNone}, start + 0.45);
        tl.fromTo($purple, 0.4, {x: 0}, {x: -30, ease: Linear.easeNone}, start + 0.45);
        tl.to($blue, 0.4, {x: -60, ease: Linear.easeNone}, start + 0.45);
        tl.fromTo($red, 0.4, {x: 0}, {x: 30, ease: Linear.easeNone}, start + 0.45);
        // ext return
        tl.to($green, 0.4, {x: 30, ease: Linear.easeNone}, start + 0.9);
        tl.to($purple, 0.4, {x: 0, ease: Linear.easeNone}, start + 0.9);
        tl.to($blue, 0.4, {x: -30, ease: Linear.easeNone}, start + 0.9);
        tl.to($red, 0.4, {x: 0, ease: Linear.easeNone}, start + 0.9);
        // center again
        tl.to($green, 0.4, {x: 0, ease: Linear.easeNone}, start + 1.35);
        tl.to($blue, 0.4, {x: 0, ease: Linear.easeNone}, start + 1.35);
    });
</script>

